import React ,{useState,useContext} from 'react';
import { FormLabel } from "@material-ui/core";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import Dropzone from 'react-dropzone';
import  StepperNavigationButtons  from 'views/Employee/StepperNavigationButtons';
import {EmployeeContext} from 'views/Employee/AddEmployee'
import * as Yup from 'yup'
import { Formik, Form } from 'formik'

const ValidationSchema = Yup.object().shape({
  fullName: Yup.string()
    .min(2,'too Short!')
    .required('Required'),
  preferredName: Yup.string(),
  birthDate: Yup.date()
    .required('Required'),

}); 
function CurrentAddress(props) {
  const [employeeData,setEmployeeData]=useContext(EmployeeContext)
  const [activeStep, setActiveStep] = useContext(StepContext);
    const [skipped, setSkipped] = useState(new Set());
    const [filename,setFilename]=useState('')
    const [file,setFile]=useState('')

    function handleOnDrop(files,rejectedFiles){
        const curfile=files[0]
        console.log(files[0].name)
        setFilename(files[0].name)

        // this.setState({imagename:files[0].name})
        const reader=new FileReader()
        reader.addEventListener("load",()=>{
            console.log("ondrop called")

        setFile(reader.result)
            // this.setState({
            //       image:reader.result
            //   })
         },false)
              reader.readAsDataURL(curfile)
    }
    return (
        <div>
          <Formik
            initialValues={employeeData}
            // validationSchema={ValidationSchema}
            onSubmit={values => {
              //handleNext()
              let newSkipped = skipped;
              const isStepSkipped = step => {
                return skipped.has(step);
              };
              if (isStepSkipped(activeStep)) {
                newSkipped = new Set(newSkipped.values());
                newSkipped.delete(activeStep);
              }
              setActiveStep(prevActiveStep => prevActiveStep + 1);
              setSkipped(newSkipped);


              alert("fd")
              setEmployeeData({...employeeData,
                fullName:values.fullName,
                maritalStatus:values.maritalStatus,
                preferredName:values.preferredName,
                birthDate:birthDate,
                gender:values.gender})
              console.log("information",employeeData)
              }} 
            render= {({errors,values, touched ,handleChange, handleSubmit}) => (
              <Form>
               <GridContainer>{
                console.log("actual2",employeeData)}
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="Street 1"
                    id="street1"
                    formControlProps={{
                      fullWidth: true
                    }}
                    // inputProps={{
                    //   disabled: true
                    // }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="Street 2"
                    id="street2"
                    formControlProps={{
                      fullWidth: true
                    }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="City"
                    id="city"
                    formControlProps={{
                      fullWidth: true
                    }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="State"
                    id="state"
                    formControlProps={{
                      fullWidth: true
                    }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="Country"
                    id="country"
                    formControlProps={{
                      fullWidth: true
                    }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Image</FormLabel>
                <Dropzone onDrop={handleOnDrop} multiple="true">

                {({getRootProps, getInputProps}) => (
                    <section>
                    <div {...getRootProps()}>
                        <input {...getInputProps()} />
                        <p>{filename===""?"Drag and drop file here, or click to select file":filename}</p>

                    </div>
                    </section>
                )}
                </Dropzone>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                <StepperNavigationButtons/>
                </GridItem>
              </GridContainer>
              </Form>
               )
               } 
            >

              </Formik>



    </div>
    );
}

export default CurrentAddress;
