import React ,{useState, useContext} from 'react';
// import { makeStyles } from "@material-ui/core/styles";
import { FormLabel,RadioGroup, Radio, FormControlLabel, Button, TextField } from "@material-ui/core";
import { Field, Form } from "formik"
// core components
import GridItem from "components/Grid/GridItem.js";
import Grid from "components/Grid/Grid.js";
import GridContainer from "components/Grid/GridContainer.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import DateFnsUtils from "@date-io/date-fns";
import { format } from "date-fns"
import { DatePicker, MuiPickersUtilsProvider} from "material-ui-pickers";
import { Formik } from 'formik'
import {EmployeeValidation} from 'lib/Yup/EmployeeValidation'  
import {EmployeeContext} from 'views/Employee/AddEmployee'
import  StepperNavigationButtons  from 'views/Employee/StepperNavigationButtons';
import * as Yup from 'yup'
import {StepContext} from 'views/Employee/AddEmployee'
// const styles = {
//     cardCategoryWhite: {
//       color: "rgba(255,255,255,.62)",
//       margin: "0",
//       fontSize: "14px",
//       marginTop: "0",
//       marginBottom: "0"
//     },
//     cardTitleWhite: {
//       color: "#FFFFFF",
//       marginTop: "0px",
//       minHeight: "auto",
//       fontWeight: "300",
//       fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
//       marginBottom: "3px",
//       textDecoration: "none"
//     }
//   };
const ValidationSchema = Yup.object().shape({
  fullName: Yup.string()
    .min(2,'too Short!')
    .required('Required'),
  // preferredName: Yup.string(),
  // birthDate: Yup.date()
  //   .required('Required'),

}); 
const initialValues2={
  fullname:'',
preferredName:'',
birthDate:'',
gender:'',
maritalStatus:'',}
// const useStyles = makeStyles(styles);
function EmployeeInformation(props) {
    const [employeeData,setEmployeeData]=useContext(EmployeeContext)
    const [activeStep, setActiveStep] = useContext(StepContext);
    const [skipped, setSkipped] = useState(new Set());
    // const classes = useStyles();
    const [birthDate,setBirthDate]=useState(new Date())
    // console.log(employeeData)
    function handleDateChange(date){
        // setBirthDate(format(date, 'dd/MM/yyyy'))
        setBirthDate(date)
    }
    return (
        <div>
          <Formik
        initialValues={{
          name:'',
          description:'',
          price:'',
          // category:'veg',
          // expirydate:new Date(),
          // image:null
        }}
        // validationSchema={validationSchema}
        onSubmit={values => {
         console.log(values)

        alert("Data has been inserted")
        }} >

        {({ errors,values, touched ,handleChange, handleSubmit}) => (

          <Form>

            <Grid container spacing={3}>

              <Grid item xs={12} sm={12} md={7}>
                {/* <Paper> */}
                  <Field fullWidth
                    id="name"
                    name="name"
                    label="Name"
                    component={TextField}
                    margin="normal"
                  />
              {/* </Paper> */}
              </Grid>
              <Grid item xs={12} sm={12} md={5}>
                {/* <Paper> */}
                  <Field fullWidth
                    id="price"
                    name="price"
                    type="number"
                    component={TextField}
                    label="Price"
                    margin="normal"
                />
                {/* </Paper> */}
              </Grid>
              <Grid item xs={12} sm={12} md={12}>
                  {/* <Paper> */}
                    <Field fullWidth
                      id="description"
                      name="description"
                      component={TextField}
                      label="Description"
                      multiline
                      rows="3"
                      margin="normal"
                  />
                  {/* </Paper> */}
              </Grid>


            <Grid item xs={12} sm={12} md={3}>
              {/* <Paper> */}
                <Button type="submit" variant="contained" color="primary"  style={{align:'left',backgroundColor:'#00acc1'}}>
                  Add
                </Button>
              {/* </Paper> */}
            </Grid>
          </Grid>


        </Form>
      )}

      </Formik>
              </div>
    );
}

export default EmployeeInformation;
