import React ,{useState} from 'react';
import { makeStyles } from "@material-ui/core/styles";
import { InputLabel,FormLabel,RadioGroup, Radio, FormControlLabel } from "@material-ui/core";
import { Field } from "formik"
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import Button from "components/CustomButtons/Button.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardAvatar from "components/Card/CardAvatar.js";
import CardBody from "components/Card/CardBody.js";
import CardFooter from "components/Card/CardFooter.js";
import avatar from "assets/img/faces/marc.jpg";
import DateFnsUtils from "@date-io/date-fns";

import { DatePicker, MuiPickersUtilsProvider} from "material-ui-pickers";
import { KeyboardDatePicker } from '@material-ui/pickers'

const styles = {
    cardCategoryWhite: {
      color: "rgba(255,255,255,.62)",
      margin: "0",
      fontSize: "14px",
      marginTop: "0",
      marginBottom: "0"
    },
    cardTitleWhite: {
      color: "#FFFFFF",
      marginTop: "0px",
      minHeight: "auto",
      fontWeight: "300",
      fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
      marginBottom: "3px",
      textDecoration: "none"
    }
  };

const useStyles = makeStyles(styles);
function EmployeeInformation(props) {

    const classes = useStyles();
    const [birthDate,setBirthDate]=useState(new Date())
    handleDateChange=(date)=>{
        this.setState({
            expirydate:date
        }) 
    }
    return (
        <div>
        <GridContainer>
        <GridItem xs={12} sm={12} md={8}>
          <Card>
            <CardHeader color="primary">
              <h4 className={classes.cardTitleWhite}>Add Employee</h4>
              <p className={classes.cardCategoryWhite}>Employee Information</p>
            </CardHeader>
            <CardBody>
              <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="Full Name(As on your ID)"
                    id="fullName"
                    formControlProps={{
                      fullWidth: true
                    }}
                    // inputProps={{
                    //   disabled: true
                    // }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="Preferred Name"
                    id="preferredName"
                    formControlProps={{
                      fullWidth: true
                    }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                    <FormLabel component="legend" style={{textAlign:'left'}}>Birth Date</FormLabel>
                    <MuiPickersUtilsProvider utils={DateFnsUtils} >
                         <DatePicker name="birthDate" id="birthDate" fullWidth 
                         value={birthDate} 
                         onChange={handleDateChange} 
                         />
                         {/* <KeyboardDatePicker
          margin="normal"
          id="date-picker-dialog"
          label="Date picker dialog"
          format="MM/dd/yyyy"
        //   value={selectedDate}
        //   onChange={handleDateChange}
          KeyboardButtonProps={{
            'aria-label': 'change date',
          }}
        /> */}
                    </MuiPickersUtilsProvider>
                </GridItem>
              </GridContainer>

              <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Gender</FormLabel>
                    <Field fullWidth
                      id="gender"
                      // aria-label="gender"
                    //   value={values.gender}
                    //   error={errors.gender}
                      component={RadioGroup}
                      row
                    //   onChange={handleChange}
                    >
                    <FormControlLabel
                        id="gender"
                        label="Male"
                        control={<Radio id="gender" />}
                        value="male"
                      />
                      <FormControlLabel
                        id="gender"
                        value="female"
                        control={<Radio id="gender"/>}
                        label="Female"
                      />
                    </Field>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Marital Status</FormLabel>
                    <Field fullWidth
                      id="maritalStatus"
                      // aria-label="maritalStatus"
                    //   value={values.maritalStatus}
                    //   error={errors.maritalStatus}
                      component={RadioGroup}
                      row
                    //   onChange={handleChange}
                    >
                    <FormControlLabel
                        id="maritalStatus"
                        label="Married"
                        control={<Radio id="maritalStatus" />}
                        value="married"
                      />
                      <FormControlLabel
                        id="maritalStatus"
                        value="single"
                        control={<Radio id="maritalStatus"/>}
                        label="Single"
                      />
                    <FormControlLabel
                        id="maritalStatus"
                        value="other"
                        control={<Radio id="maritalStatus"/>}
                        label="Other"
                      />
                    </Field>
                </GridItem>

              </GridContainer>

            </CardBody>
            {/* <CardFooter>
              <Button color="primary">Update Profile</Button>
            </CardFooter> */}
          </Card>
        </GridItem>
        {/* <GridItem xs={12} sm={12} md={4}>
          <Card profile>
            <CardAvatar profile>
              <a href="#pablo" onClick={e => e.preventDefault()}>
                <img src={avatar} alt="..." />
              </a>
            </CardAvatar>
            <CardBody profile>
              <h6 className={classes.cardCategory}>CEO / CO-FOUNDER</h6>
              <h4 className={classes.cardTitle}>Alec Thompson</h4>
              <p className={classes.description}>
                Don{"'"}t be scared of the truth because we need to restart the
                human foundation in truth And I love you like Kanye loves Kanye
                I love Rick Owens’ bed design but the back is...
              </p>
              <Button color="primary" round>
                Follow
              </Button>
            </CardBody>
          </Card>
        </GridItem> */}
      </GridContainer>
    </div>
    );
}

export default EmployeeInformation;
