import React, { useState, useContext } from 'react';
import { FormLabel } from "@material-ui/core";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import StepperNavigationButtons from 'components/Stepper/StepperNavigationButtons';
import { StepContext, EmployeeContext, TitleContext } from 'views/Employee/Add'
import * as Yup from 'yup'
import { Formik, Form, Field, ErrorMessage } from 'formik'
import { TextField } from 'formik-material-ui';
import { makeStyles } from "@material-ui/core/styles";
import CustomDropzone from 'components/Dropzone/Dropzone'
import api, { toFormData } from '../../lib/axios';

const useStyles = makeStyles({
  field: {
    marginTop: "32px"
  }
})

const validationSchema = Yup.object().shape({
  // street1: Yup.string().required('Required'),
  city: Yup.string().required('Required')
  //  addressProof: Yup.string().required('Required')

});

function CurrentAddress(props) {

  const classes = useStyles()
  const [employeeData, setEmployeeData] = useContext(EmployeeContext)
  const [activeStep, setActiveStep] = useContext(StepContext);
  const [skipped, setSkipped] = useState(new Set());
  const [file,setFile]=useState()
  const [title, setTitle] = useContext(TitleContext);
  const fileList = []
  const  fileAttachments=[]
  setTitle('Current Address')

  return (
    <Formik
      initialValues={employeeData}
      validationSchema={validationSchema}
      onSubmit={values => {
        //handleNext()
        let newSkipped = skipped;
        const isStepSkipped = step => {
          return skipped.has(step);
        };
        if (isStepSkipped(activeStep)) {
          newSkipped = new Set(newSkipped.values());
          newSkipped.delete(activeStep);
        }
        setActiveStep(prevActiveStep => prevActiveStep + 1);
        setSkipped(newSkipped);
        setEmployeeData({
          ...employeeData,
          currentAddress:{
          street1: values.currentAddress.street1,
          street2: values.street2,
          city: values.city,
          state: values.state,
          country: values.country
          },
          employeeAttachments:[...employeeData.employeeAttachments,values.attachments]
          // currentStreet1: values.currentStreet1,
          // currentStreet2: values.currentStreet2,
          // currentCity: values.currentCity,
          // currentState: values.currentState,
          // currentCountry: values.currentCountry,
          // currentAddressProof: values.currentAddressProof
        })

        console.log("2",values.attachments)
        // file.map(file=>{
        //   let test = {
        //         file,
        //         type:'Current Address Proof'
        //       }
        //       const fileData=toFormData(test)
        //       console.log(fileData)
        //        api.post('employees/file',fileData).then(res=>{
        //         const fileAttachments=res.data.data
        //         console.log("sho",fileAttachments)
        //         setEmployeeData({...employeeData,employeeAttachments:[...employeeData.employeeAttachments,fileAttachments]})
        //         // setLoad({ ...load, attachments: [...load.attachments, data] });
        //         console.log("current",res.data.data)
        //     }).catch(err=>{console.log("err",err)})

        // })


      }}
      render={({ values, setFieldValue }) => {
        return (<Form>
          <GridContainer>

            <GridItem xs={12} sm={12} md={6}>
              <Field
                label="Street 1"
                id="street1"
                name="currentAddress.street1"
                className={classes.field}
                component={TextField}
                fullWidth
              />
            </GridItem>

            <GridItem xs={12} sm={12} md={6}>
              <Field
                label="Street 2"
                id="street2"
                name="street2"
                className={classes.field}
                component={TextField}
                fullWidth
              />
            </GridItem>

            <GridItem xs={12} sm={12} md={4}>
              <Field
                label="City"
                id="city"
                name="city"
                className={classes.field}
                component={TextField}
                fullWidth
              />
            </GridItem>

            <GridItem xs={12} sm={12} md={4}>
              <Field
                label="State"
                id="state"
                name="state"
                className={classes.field}
                component={TextField}
                fullWidth
              />
            </GridItem>

            <GridItem xs={12} sm={12} md={4}>
              <Field
                label="Country"
                id="country"
                name="country"
                className={classes.field}
                component={TextField}
                fullWidth
              />
            </GridItem>

            <GridItem xs={12} sm={12} md={12}>
              <FormLabel component="legend" style={{ textAlign: 'left' }} className={classes.field}>Current Address Proof</FormLabel>
              <CustomDropzone list={values.addressProof?values.addressProof:[]} callBack={files => {

                files.map(file => {
                  var exist = 0
                  fileList.map(existingFile => {

                    if (existingFile.name === file.name && existingFile.size === file.size) {
                      exist = 1;
                      // alert("File has already selected")
                    }
                  })
                  if (exist === 1) {

                    exist = 0;
                  }
                  else {
                    fileList.push(file)
                    let test = {
                      file,
                      type:'Current Address Proof'
                    }
                    const fileData=toFormData(test)
                    // console.log(fileData)
                    api.post('employees/file',fileData).then(res=>{
                       fileAttachments.push(res.data.data)
                    setFieldValue('attachments',fileAttachments)
                      // console.log("1",fileAttachments)

                  }).catch(err=>{console.log("err",err)})

                  }
                })
                console.log("filelist", fileList)
                setFieldValue('addressProof', fileList)
                setFile(fileList)
              }} 

              />

            </GridItem>

            <GridItem xs={12} sm={12} md={12}>
              <StepperNavigationButtons />
            </GridItem>

          </GridContainer>
        </Form>)
      }}
    >
    </Formik>
  );
}

export default CurrentAddress;
