import React, { useState, useContext } from 'react';
import { FormLabel } from "@material-ui/core";
import { TextField } from 'formik-material-ui'; 
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Dropzone from 'react-dropzone';
import StepperNavigationButtons from 'components/Stepper/StepperNavigationButtons';
import { StepContext, EmployeeContext } from 'views/Employee/Add'
import * as Yup from 'yup'
import { Formik, Form, Field } from 'formik'
import { makeStyles } from "@material-ui/core/styles";

const useStyles=makeStyles({
  field:{
    marginTop: "32px"
  }
})

const validationSchema = Yup.object().shape({
  street1: Yup.string()
    .required('Required'),
  city: Yup.string()
    .required('Required'),
  //  addressProof: Yup.file().required('Required')

});
function PermanentAddress(props) {
  const classes=useStyles()
  const [employeeData, setEmployeeData] = useContext(EmployeeContext)
  const [activeStep, setActiveStep] = useContext(StepContext);
  const [skipped, setSkipped] = useState(new Set());
  const [filename, setFilename] = useState('')
  const [file, setFile] = useState('')

  function handleOnDrop(files, rejectedFiles) {
    const curfile = files[0]
    setFilename(files[0].name)
    const reader = new FileReader()
    reader.addEventListener("load", () => {
      setFile(reader.result)
    }, false)
    reader.readAsDataURL(curfile)
  }
  return (

      <Formik
        initialValues={employeeData}
        validationSchema={validationSchema}
        onSubmit={values => {
          //handleNext()
          let newSkipped = skipped;
          const isStepSkipped = step => {
            return skipped.has(step);
          };
          if (isStepSkipped(activeStep)) {
            newSkipped = new Set(newSkipped.values());
            newSkipped.delete(activeStep);
          }
          setActiveStep(prevActiveStep => prevActiveStep + 1);
          setSkipped(newSkipped);
          setEmployeeData({
            ...employeeData,
            permanentStreet1: values.street1,
            permanentStreet2: values.street2,
            permanentCity: values.city,
            permanentState: values.state,
            permanentCountry: values.country,
            permanentAddressProof: filename
          })
        }}
        render={() => (
          <Form>
            <GridContainer>

              <GridItem xs={12} sm={12} md={12}>
                <Field
                  label="Street 1"
                  id="street1"
                  name="street1"
                  className={classes.field}
                  component={TextField}
                  fullWidth
                />
              </GridItem>

              <GridItem xs={12} sm={12} md={12}>
                <Field
                  label="Street 2"
                  id="street2"
                  name="street2"
                  className={classes.field}
                  component={TextField}
                  fullWidth
                />
              </GridItem>

              <GridItem xs={12} sm={12} md={12}>
                <Field
                  label="City"
                  id="city"
                  name="city"
                  className={classes.field}
                  component={TextField}
                  fullWidth
                />
              </GridItem>

              <GridItem xs={12} sm={12} md={12}>
                <Field
                  label="State"
                  id="state"
                  name="state"
                  className={classes.field}
                  component={TextField}
                  fullWidth
                />
              </GridItem>

              <GridItem xs={12} sm={12} md={12}>
                <Field
                  label="Country"
                  id="country"
                  name="country"
                  className={classes.field}
                  component={TextField}
                  fullWidth
                />
              </GridItem>

              <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{ textAlign: 'left' }} 
                  className={classes.field}>Image</FormLabel>
                <Dropzone onDrop={handleOnDrop} multiple="true">
                  {({ getRootProps, getInputProps }) => (
                    <section>
                      <div {...getRootProps()}>
                        <input {...getInputProps()} />
                        <div>{filename === "" ? "Drag and drop file here, or click to select file" : filename}</div>
                      </div>
                    </section>
                  )}
                </Dropzone>
              </GridItem>
              <GridItem xs={12} sm={12} md={12}>
                <StepperNavigationButtons />
              </GridItem>
            </GridContainer>
          </Form>
        )}
      >
      </Formik>
  );
}

export default PermanentAddress;
