import React, {useState} from 'react';
import { Typography } from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';

function Accordion({title,children}) {
    const [showContent,setShowContent]=useState(false)
    return (
        <div>
            <div style={{backgroundColor:'grey',padding:'10px',borderRadius:'1%'}}>
                <Typography style={{display:'inline'}}>title</Typography>
                {showContent?
                <ExpandLessIcon style={{float:'right',padding:'1px'}} onClick={()=>{setShowContent(!showContent)}}>+</ExpandLessIcon>: 
                <ExpandMoreIcon style={{float:'right',padding:'1px'}} onClick={()=>{setShowContent(!showContent)}}>+</ExpandMoreIcon>}


        </div>
        {showContent?<div style={{backgroundColor:'grey',padding:'10px',borderRadius:'1%',marginTop:'10px'}}>{children}</div>:""}
        </div>
    );
}

export default Accordion;
