import React from 'react';
import EmployeeInformation from 'components/AddEmployeeForms/EmployeeInformation';
import { Formik } from 'formik'
import {EmployeeValidation} from 'lib/Yup/EmployeeValidation'  
import CurrentAddress from 'components/AddEmployeeForms/CurrentAddress';
import PermanentAddress from 'components/AddEmployeeForms/PermanentAddress';
import EmergencyContact from 'components/AddEmployeeForms/EmergencyContact';
import JobDetails from 'components/AddEmployeeForms/JobDetails';
import IdProof from 'components/AddEmployeeForms/IdProof';
import EmployeePicture from 'components/AddEmployeeForms/EmployeePicture';
import Feedback from 'components/AddEmployeeForms/Feedback';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
const useStyles = makeStyles(theme => ({
    root: {
      width: '100%',
    },
    button: {
      marginRight: theme.spacing(1),
    },
    instructions: {
      marginTop: theme.spacing(1),
      marginBottom: theme.spacing(1),
    },
    cardCategoryWhite: {
      color: "rgba(255,255,255,.62)",
      margin: "0",
      fontSize: "14px",
      marginTop: "0",
      marginBottom: "0"
    },
    cardTitleWhite: {
      color: "#FFFFFF",
      marginTop: "0px",
      minHeight: "auto",
      fontWeight: "300",
      fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
      marginBottom: "3px",
      textDecoration: "none"
    }
  }));

  function getSteps() {
    return ['Employee Information', 'Current Address', 'Permanent Address', 'Emergency Contact', 'Job Details', 'Id Prrof', 'Employee Picture', 'Feedback'];
  }

  function getStepContent(step) {
    switch (step) {
        case 0:
            return <EmployeeInformation/>;
        case 1:
            return <CurrentAddress/>;
        case 2:
            return <PermanentAddress/>;
        case 3:
            return <EmergencyContact/>;
        case 4:
            return <JobDetails/>;
        case 5:
            return <IdProof/>;
        case 6:
            return <EmployeePicture/>;
        case 7:
            return <Feedback/>;  
        default:
            return 'Unknown step';
    }
  }

const initialValues={
    fullName:'',
    preferredName:'',
    birthDate:'',
    gender:'',
    maritalStatus:'',
    currentStreet1:'',
    currentStreet2:'',
    currentCity:'',
    currentState:'',
    currentCountry:'',
    currentAddressProof:'',
    permanentStreet1:'',
    permanentStreet2:'',
    permanentCity:'',
    permanentState:'',
    permanentCountry:'',
    permanentAddressProof:'',
    emergencyName1:'',
    emergencyMobile1:'',
    emergencyRelationship1:'',
    emergencyName2:'',
    emergencyMobile2:'',
    emergencyRelationship2:'',
    jobHireDate:'',
    jobSalary:'',
    jobCurrentsalary:'',
    jobBond:'',
    idProof:'',
    picture:'',
    feedback:''

}

// const initialValues={
//     employeeInformation:{
//         fullName:'',
//         preferredName:'',
//         birthDate:'',
//         gender:'',
//         maritalStatus:''
//     },
//     currentAddress:{
//         street1:'',
//         street2:'',
//         city:'',
//         state:'',
//         country:'',
//         addressProof:''
//     },
//     permanentAddress:{
//         street1:'',
//         street2:'',
//         city:'',
//         state:'',
//         country:'',
//         addressProof:''
//     },
//     emergencyContact:{
//         name1:'',
//         mobile1:'',
//         relationship1:'',
//         name2:'',
//         mobile2:'',
//         relationship2:''
//     },
//     jobDetails:{
//         hireDate:'',
//         salary:'',
//         currentSalary:'',
//         bond:''
//     },
//     idProof:{
//         idProof:''
//     },
//     employeePicture:{
//         picture:''
//     },
//     feedback:{
//         feedback:''
//     }    
// }

function AddEmployee(props) {
    const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);
  const [skipped, setSkipped] = React.useState(new Set());
  const steps = getSteps();

  const isStepOptional = step => {
    return step === 7;
  };

  const isStepSkipped = step => {
    return skipped.has(step);
  };

  const handleNext = () => {
    let newSkipped = skipped;
    if (isStepSkipped(activeStep)) {
      newSkipped = new Set(newSkipped.values());
      newSkipped.delete(activeStep);
    }

    setActiveStep(prevActiveStep => prevActiveStep + 1);
    setSkipped(newSkipped);
  };

  const handleBack = () => {
    setActiveStep(prevActiveStep => prevActiveStep - 1);
  };

  const handleSkip = () => {
    if (!isStepOptional(activeStep)) {
      // You probably want to guard against something like this,
      // it should never occur unless someone's actively trying to break something.
      throw new Error("You can't skip a step that isn't optional.");
    }

    setActiveStep(prevActiveStep => prevActiveStep + 1);
    setSkipped(prevSkipped => {
      const newSkipped = new Set(prevSkipped.values());
      newSkipped.add(activeStep);
      return newSkipped;
    });
  };

  const handleReset = () => {
    setActiveStep(0);
  };

    return (
      <Card>
            <CardHeader color="primary">
              <h4 className={classes.cardTitleWhite}>Add Employee</h4>
              <p className={classes.cardCategoryWhite}>Employee Information</p>
            </CardHeader>
            <CardBody>
      <GridContainer>
        <GridItem xs={12} sm={12} md={12}>

    <div className={classes.root}>

          <Stepper activeStep={activeStep}>
        {steps.map((label, index) => {
          const stepProps = {};
          const labelProps = {};
          if (isStepOptional(index)) {
            labelProps.optional = <Typography variant="caption">Optional</Typography>;
          }
          if (isStepSkipped(index)) {
            stepProps.completed = false;
          }
          return (
            <Step key={label} {...stepProps}>
              <StepLabel {...labelProps}>{label}</StepLabel>
            </Step>
          );
        })}
      </Stepper>
      <div>

        {activeStep === steps.length ? (
          <div>
            <Typography className={classes.instructions}>
              All steps completed - you&apos;re finished
            </Typography>
            <Button onClick={handleReset} className={classes.button}>
              Reset
            </Button>
          </div>
        ) : (
          <div>
            <Typography className={classes.instructions}>
            {getStepContent(activeStep)}</Typography>
            <div>
              <Button disabled={activeStep === 0} onClick={handleBack} className={classes.button}>
                Back
              </Button>
              {isStepOptional(activeStep) && (
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleSkip}
                  className={classes.button}
                >
                  Skip
                </Button>
              )}

              <Button
                variant="contained"
                color="primary"
                onClick={handleNext}
                className={classes.button}
              >
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>

            </div>
          </div>
        )}
      </div>

    </div>

          </GridItem>

      </GridContainer>
      </CardBody>
            {/* <CardFooter>
              <Button color="primary">Update Profile</Button>
            </CardFooter> */}
          </Card>
    );
}

export default AddEmployee;
