import React ,{useState, useContext} from 'react';
import { FormLabel,RadioGroup, Radio, FormControlLabel, Button, TextField } from "@material-ui/core";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import DateFnsUtils from "@date-io/date-fns";
import { DatePicker, MuiPickersUtilsProvider} from "material-ui-pickers";
import { Formik , Field, Form} from 'formik'
import {EmployeeContext} from 'views/Employee/AddEmployee'
import  StepperNavigationButtons  from 'views/Employee/StepperNavigationButtons';
import * as Yup from 'yup'
import {StepContext} from 'views/Employee/AddEmployee'

const ValidationSchema = Yup.object().shape({
  hireDate: Yup.date()
    .required('Required'),
  salary: Yup.number(),
  currentSalary: Yup.number()
    .required('Required'),

}); 
function JobDetails(props) {
  const [employeeData,setEmployeeData]=useContext(EmployeeContext)
  const [activeStep, setActiveStep] = useContext(StepContext);
  const [skipped, setSkipped] = useState(new Set());
    const [hireDate,setHireDate]=useState(new Date())
    function handleDateChange(date){
        // setHireDate(format(date, 'dd/MM/yyyy'))
        setHireDate(date)
    }
    return (
        <div>
       <Formik
            initialValues={employeeData}
            // validationSchema={ValidationSchema}
            onSubmit={values => {
              //handleNext()
              let newSkipped = skipped;
              const isStepSkipped = step => {
                return skipped.has(step);
              };
              if (isStepSkipped(activeStep)) {
                newSkipped = new Set(newSkipped.values());
                newSkipped.delete(activeStep);
              }
              setActiveStep(prevActiveStep => prevActiveStep + 1);
              setSkipped(newSkipped);


              alert("fd")
              setEmployeeData({...employeeData,
                fullName:values.fullName,
                maritalStatus:values.maritalStatus,
                preferredName:values.preferredName,
                birthDate:birthDate,
                gender:values.gender})
              console.log("information",employeeData)
              }} 
            render= {({errors,values, touched ,handleChange, handleSubmit}) => (
              <Form>
              <GridContainer>
              <GridItem xs={12} sm={12} md={12}>
                    <FormLabel component="legend" style={{textAlign:'left'}}>Hire Date</FormLabel>
                    <MuiPickersUtilsProvider utils={DateFnsUtils} >

                         <DatePicker 
                         name="hireDate" 
                         id="hireDate" 
                          fullWidth 
                         value={hireDate} 
                         format='dd-MM-yyyy' 
                         placeholder='Enter date' 
                         onChange={handleDateChange} 
                         label="Hire Date"
                         />

                    </MuiPickersUtilsProvider>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  {/* we can use CustomInput also insted of field */}
                  <Field
                    label="Salary"
                    // placeholder="Please Provide salary when you have joined"

                    helperText="Please Provide salary when you have joined"
                    id="salary"
                    name="salary"
                    value={values.salary}
                    component={TextField}
                    onChange={handleChange}
                    fullWidth

                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <Field
                    label="Current Salary"
                    id="currentSalary"
                    helperText="Please Provide salary when you have joined"
                    value={values.currentSalary}
                    name="currentSalary"
                    component={TextField}
                    onChange={handleChange}
                    fullWidth
                  />
                </GridItem>



                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Have you signed/agreed any bond?</FormLabel>
                    <Field fullWidth
                      id="bond"
                      name="bond"

                      value={values.bond}
                      error={errors.bond}
                      component={RadioGroup}
                      row

                      onChange={handleChange}
                    >
                    <FormControlLabel
                        id="bond"
                        label="1 Year"
                        control={<Radio id="bond" />}
                        value="1"
                      />
                      <FormControlLabel
                        id="bond"
                        value="2"
                        control={<Radio id="bond"/>}
                        label="2 Year"
                      />

                      <FormControlLabel
                        id="bond"
                        value="no"
                        control={<Radio id="bond"/>}
                        label="No"
                      />
                    </Field>
                </GridItem>

                <GridItem>
                <StepperNavigationButtons />
                {/* <Button type="submit">fds</Button> */}
                </GridItem>
              </GridContainer>
              </Form>
               )
               } 
            >

              </Formik>









    </div>
    );
}

export default JobDetails;
