import React ,{useState, useContext} from 'react';
import { FormLabel,RadioGroup, Radio, FormControlLabel, Button, TextField } from "@material-ui/core";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import DateFnsUtils from "@date-io/date-fns";
import { DatePicker, MuiPickersUtilsProvider} from "material-ui-pickers";
import { Formik , Field, Form} from 'formik'
import {EmployeeContext} from 'views/Employee/AddEmployee'
import  StepperNavigationButtons  from 'views/Employee/StepperNavigationButtons';
import * as Yup from 'yup'
import {StepContext} from 'views/Employee/AddEmployee'

const ValidationSchema = Yup.object().shape({
  hireDate: Yup.date()
    .required('Required'),
  salary: Yup.number(),
  currentSalary: Yup.number()
    .required('Required'),

}); 
function JobDetails(props) {
  const [employeeData,setEmployeeData]=useContext(EmployeeContext)
  const [activeStep, setActiveStep] = useContext(StepContext);
  const [skipped, setSkipped] = useState(new Set());
    const [hireDate,setHireDate]=useState(new Date())
    function handleDateChange(date){
        // setHireDate(format(date, 'dd/MM/yyyy'))
        setHireDate(date)
    }
    return (
        <div>
       <Formik
            initialValues={employeeData}
            // validationSchema={ValidationSchema}
            onSubmit={values => {
              //handleNext()
              let newSkipped = skipped;
              const isStepSkipped = step => {
                return skipped.has(step);
              };
              if (isStepSkipped(activeStep)) {
                newSkipped = new Set(newSkipped.values());
                newSkipped.delete(activeStep);
              }
              setActiveStep(prevActiveStep => prevActiveStep + 1);
              setSkipped(newSkipped);


              alert("fd")
              setEmployeeData({...employeeData,
                fullName:values.fullName,
                maritalStatus:values.maritalStatus,
                preferredName:values.preferredName,
                birthDate:birthDate,
                gender:values.gender})
              console.log("information",employeeData)
              }} 
            render= {({errors,values, touched ,handleChange, handleSubmit}) => (
              <Form>
              <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                  {/* we can use CustomInput also insted of field */}
                  <Field
                    label="Salary"
                    // placeholder="Please Provide salary when you have joined"

                    helperText="Please Provide salary when you have joined"
                    id="fullName"
                    name="fullName"
                    value={values.fullName}
                    component={TextField}
                    onChange={handleChange}
                    fullWidth

                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <Field
                    label="Current Salary"
                    id="currentSalary"
                    helperText="Please Provide salary when you have joined"
                    value={values.preferredName}
                    name="preferredName"
                    component={TextField}
                    onChange={handleChange}
                    fullWidth
                  />
                </GridItem>

                <GridItem xs={12} sm={12} md={12}>
                    <FormLabel component="legend" style={{textAlign:'left'}}>Hire Date</FormLabel>
                    <MuiPickersUtilsProvider utils={DateFnsUtils} >

                         <DatePicker 
                         name="hireDate" 
                         id="hireDate" 
                          fullWidth 
                         value={hireDate} 
                         format='dd-MM-yyyy' 
                         placeholder='Enter date' 
                         onChange={handleDateChange} 
                         label="Hire Date"
                         />

                    </MuiPickersUtilsProvider>
                </GridItem>

                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Gender</FormLabel>
                    <Field fullWidth
                      id="gender"
                      name="gender"

                      value={values.gender}
                      error={errors.gender}
                      component={RadioGroup}
                      row

                      onChange={handleChange}
                    >
                    <FormControlLabel
                        id="gender"
                        label="Male"
                        control={<Radio id="gender" />}
                        value="male"
                      />
                      <FormControlLabel
                        id="gender"
                        value="female"
                        control={<Radio id="gender"/>}
                        label="Female"
                      />
                    </Field>
                </GridItem>

                <GridItem>
                <StepperNavigationButtons />
                {/* <Button type="submit">fds</Button> */}
                </GridItem>
              </GridContainer>
              </Form>
               )
               } 
            >

              </Formik>
              <GridContainer>
              <GridItem xs={12} sm={12} md={12}>
                    {/* <FormLabel component="legend" style={{textAlign:'left'}}>Birth Date</FormLabel> */}
                    <MuiPickersUtilsProvider utils={DateFnsUtils} >
                         <DatePicker name="hireDate" id="hireDate" fullWidth 
                         value={hireDate} 
                         format='dd-MM-yyyy' 
                         placeholder='Enter date' 
                         onChange={handleDateChange} 
                         label="Hire Date"
                         helperText="Please provide date of your joining"
                         />

                    </MuiPickersUtilsProvider>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <TextField
                    label="Salary"
                    // placeholder="Please Provide salary when you have joined"
                    id="salary"
                    helperText="Please Provide salary when you have joined"
                    // formControlProps={{
                    //   fullWidth: true
                    // }}
                    fullWidth
                    // inputProps={{
                    //   disabled: true
                    // }}
                  />
                </GridItem>
                {/* Use customInput instead of textfield in case of any errors */}
                <GridItem xs={12} sm={12} md={12}>
                  <TextField
                    label="Current Salary"
                    id="currentSalary"
                    helperText="Please Provide salary when you have joined"
                    fullWidth
                    // formControlProps={{
                    //   fullWidth: true
                    // }}
                  />
                </GridItem>



                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Have you signed/agreed any bond?</FormLabel>
                    <Field fullWidth
                      id="bond"
                    //   label="Have you signed/agreed any bond?"
                      // aria-label="gender"
                    //   value={values.gender}
                    //   error={errors.gender}
                      component={RadioGroup}
                      row

                    //   onChange={handleChange}
                    >
                    <FormControlLabel
                        id="bond"
                        label="1 Year"
                        control={<Radio id="bond" />}
                        value="1"
                      />
                      <FormControlLabel
                        id="bond"
                        value="2"
                        control={<Radio id="bond"/>}
                        label="2 Year"
                      />

                    <FormControlLabel
                        id="bond"
                        value="0"
                        control={<Radio id="bond"/>}
                        label="No"
                      />
                    </Field>
                </GridItem>

              </GridContainer>

             {/*</CardBody>
            <CardFooter>
              <Button color="primary">Update Profile</Button>
            </CardFooter> 
          </Card>
        </GridItem> */}
        {/* <GridItem xs={12} sm={12} md={4}>
          <Card profile>
            <CardAvatar profile>
              <a href="#pablo" onClick={e => e.preventDefault()}>
                <img src={avatar} alt="..." />
              </a>
            </CardAvatar>
            <CardBody profile>
              <h6 className={classes.cardCategory}>CEO / CO-FOUNDER</h6>
              <h4 className={classes.cardTitle}>Alec Thompson</h4>
              <p className={classes.description}>
                Don{"'"}t be scared of the truth because we need to restart the
                human foundation in truth And I love you like Kanye loves Kanye
                I love Rick Owensâ€™ bed design but the back is...
              </p>
              <Button color="primary" round>
                Follow
              </Button>
            </CardBody>
          </Card>
        </GridItem> */}
      {/* </GridContainer> */}
    </div>
    );
}

export default JobDetails;
