import React, { useState } from 'react';
import { Typography } from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
    root: {
        backgroundColor: '#eeeeee',
        padding: '15px',
        borderRadius: '1%',
        boxShadow:'10px 5px 5px red ',
        '&:hover': {
            backgroundColor: '#ffffff',
            // border:'1px solid #eeeeee',

          }
    },
    accordionTitle: {
        display: 'inline'
    },
    accordionBody: {
        marginTop: '20px',
        minHeight: '100px'
    },
    icon: {
        float: 'right'
    }
})

function Accordion({ title, children }) {

    const classes = useStyles()
    const [showContent, setShowContent] = useState(false)
    return (
        <div className={classes.root}>
            <Typography className={classes.accordionTitle}>title</Typography>
            {showContent ?
                <ExpandLessIcon className={classes.icon} onClick={() => { setShowContent(!showContent) }} /> :
                <ExpandMoreIcon className={classes.icon} onClick={() => { setShowContent(!showContent) }} />}
            {showContent ? <div className={classes.accordionBody}>{children}</div> : ""}
        </div>
    );
}

export default React.memo(Accordion);
