import React, {useState} from 'react';
import { makeStyles } from "@material-ui/core/styles";
import { Typography } from '@material-ui/core';

const useStyles = makeStyles({
    modalWrapper: {
        background: 'white',
        border: '1px solid #d0cccc',
        boxShadow: '0 5px 8px 0 rgba(0,0,0,0.2), 0 7px 20px 0 rgba(0,0,0,0.17)',
        margin: '100px auto 0',
        transition: 'all .8s',
        width: '60%',
        height:'auto',
        transform: 'translateY(0vh)' ,
        opacity: '1'
    },

    modalHeader: {
        background: '#ffffff',
        height: 'auto',
        lineHeight: '40px',
        padding: '5px 20px',
        // textAlign: 'right',
    },


    modalBody: {
        padding: '10px 15px',
        textAlign: 'center',
    },

    modalFooter: {
        background: '#ffffff',
        height: 'auto',
        padding: '15px',
    }

})
function Modal({orderstatus,createddate, ordernumber,children}) {
    const classes = useStyles()
    const [showModal,setShowModal]=useState(false)
    return (
        <React.Fragment>
            <button onClick={()=>{setShowModal(!showModal)}}>Open</button>

            {showModal?
            <div>
            <div className="jw-modal">
            <div className="jw-modal-body">
                {children}
                <button onClick={()=>{setShowModal(!showModal)}}>CLOSE</button>
            </div>
        </div>
        <div className="jw-modal-background"></div>
        {/* <div className={classes.modalWrapper}>
                <div className={classes.modalHeader}>
                <Typography className={classes.expansionPanelSummary}>
                Order Number: {ordernumber} -{" "}
                <span>
                    {orderstatus === "Inactive" ? "Order Failed" : orderstatus} - {createddate}
                </span>
            </Typography>
                </div>
                <div className={classes.modalBody}>
                    {children}
                </div>
                <div className={classes.modalFooter}>
                    <button onClick={()=>{setShowModal(!showModal)}}>CLOSE</button>
                </div>
            </div> */}
        </div>:""} 

            </React.Fragment>

    );
}

export default Modal;
