import React, {useState} from 'react';
import { Typography } from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';

function Accordion({title,children}) {
    const [showContent,setShowContent]=useState(false)
    return (
        <div>
            <div style={{backgroundColor:'#eeeeee',padding:'15px',borderRadius:'1%'}}>
                <Typography style={{display:'inline'}}>title</Typography>
                {showContent?
                <ExpandLessIcon style={{float:'right'}} onClick={()=>{setShowContent(!showContent)}}>+</ExpandLessIcon>: 
                <ExpandMoreIcon style={{float:'right'}} onClick={()=>{setShowContent(!showContent)}}>+</ExpandMoreIcon>}
           {showContent?<div style={{marginTop:'20px',minHeight:'100px'}}>{children}</div>:""}

        </div>

        </div>
    );
}

export default Accordion;
