import React, {useState} from 'react';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
    expansionPanelSummary: {
        display: 'inline'
    },
    icon: {
        float: 'right',
    }
})

function ExpansionPanelSummary({expandIcon,children}) {
    const classes = useStyles()
    const [showContent, setShowContent] = useState(false)
    return (
        <div className={classes.expansionPanelSummary}>
           <div>{children}</div> 
            <div>
            {showContent ?
                <ExpandLessIcon className={classes.icon} onClick={() => { setShowContent(!showContent) }} /> :
                <ExpandMoreIcon className={classes.icon} onClick={() => { setShowContent(!showContent) }} />}
        </div>
        </div>
    );
}

export default ExpansionPanelSummary;
