import React, {useState} from 'react';
import { Button, Typography } from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';

function Accordion(props) {
    const [showContent,setShowContent]=useState(false)
    return (
        <div>
            <div style={{backgroundColor:'grey',padding:'10px',borderRadius:'1%'}}>
                <Typography style={{display:'inline'}}>fdsgfdfds</Typography>
            <ExpandLessIcon style={{display:'inline',float:'right',padding:'1px'}} onClick={()=>{setShowContent(!showContent)}}>+</ExpandLessIcon>
            <ExpandMoreIcon style={{display:'inline',float:'right',padding:'1px'}} onClick={()=>{setShowContent(!showContent)}}>+</ExpandMoreIcon>
        </div>
        {showContent?<div>df</div>:""}
        </div>
    );
}

export default Accordion;
