// import { makeStyles } from "@material-ui/core/styles";
import { FormLabel,RadioGroup, Radio, FormControlLabel, Button, TextField } from "@material-ui/core";
import { Field, Form } from "formik"
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import DateFnsUtils from "@date-io/date-fns";
import { format } from "date-fns"
import { DatePicker, MuiPickersUtilsProvider} from "material-ui-pickers";
import { Formik } from 'formik'
import {EmployeeValidation} from 'lib/Yup/EmployeeValidation'  
// const styles = {
//     cardCategoryWhite: {
//       color: "rgba(255,255,255,.62)",
//       margin: "0",
//       fontSize: "14px",
//       marginTop: "0",
//       marginBottom: "0"
//     },
//     cardTitleWhite: {
//       color: "#FFFFFF",
//       marginTop: "0px",
//       minHeight: "auto",
//       fontWeight: "300",
//       fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
//       marginBottom: "3px",
//       textDecoration: "none"
//     }
//   };


const initialValues={
  fullName:'',
  preferredName:'',
  birthDate:'',
  gender:'male',
  maritalStatus:'',
  // currentStreet1:'',
  // currentStreet2:'',
  // currentCity:'',
  // currentState:'',
  // currentCountry:'',
  // currentAddressProof:'',
  // permanentStreet1:'',
  // permanentStreet2:'',
  // permanentCity:'',
  // permanentState:'',
  // permanentCountry:'',
  // permanentAddressProof:'',
  // emergencyName1:'',
  // emergencyMobile1:'',
  // emergencyRelationship1:'',
  // emergencyName2:'',
  // emergencyMobile2:'',
  // emergencyRelationship2:'',
  // jobHireDate:'',
  // jobSalary:'',
  // jobCurrentsalary:'',
  // jobBond:'',
  // idProof:'',
  // picture:'',
  // feedback:''

}
// const useStyles = makeStyles(styles);
function EmployeeInformation(props) {

    // const classes = useStyles();
    const [birthDate,setBirthDate]=useState(new Date())
    function handleDateChange(date){
        // setBirthDate(format(date, 'dd/MM/yyyy'))
        setBirthDate(date)
    }
    return (
        <div>
          <Formik
            initialValues={initialValues}
            validationSchema={EmployeeValidation}
            onSubmit={values => {
              console.log("fd"+values.fullName)
              }} 
              onChange={values=>{
                console.log("ds")
              }

              }
              // component={EmployeeInformation}
              // component={CurrentAddress}
              // component={PermanentAddress}
              // component={EmergencyContact}
              // component={JobDetails}
              // component={IdProof}
              // component={EmployeePicture}
              // component={Feedback}
            >
              <Form>
        {/* <GridContainer>
        <GridItem xs={12} sm={12} md={12}>
          <Card>
            <CardHeader color="primary">
              <h4 className={classes.cardTitleWhite}>Add Employee</h4>
              <p className={classes.cardCategoryWhite}>Employee Information</p>
            </CardHeader>
            <CardBody> */}
              <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                  {/* we can use CustomInput also insted of field */}
                  <Field
                    label="Full Name(As on your ID)"
                    id="fullName"
                    name="fullName"
                    component={TextField}
                    fullWidth
                    // formControlProps={{
                    //   fullWidth: true
                    // }}
                    // inputProps={{
                    //   disabled: true
                    // }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <Field
                    label="Preferred Name"
                    id="preferredName"
                    name="preferredName"
                    component={TextField}
                    fullWidth
                    // formControlProps={{
                    //   fullWidth: true
                    // }}
                  />
                </GridItem>
               </GridContainer>
               <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                    <FormLabel component="legend" style={{textAlign:'left'}}>Birth Date</FormLabel>
                    <MuiPickersUtilsProvider utils={DateFnsUtils} >
                      <Field
                      component={DatePicker}
                      value={birthDate} 
                         format='dd-MM-yyyy' 
                         placeholder='Enter date' 
                         onChange={handleDateChange} 
                        //  label="Birth Date"
                         name="birthDate"
                          id="birthDate" 
                          fullWidth
                      ></Field>
                         {/* <DatePicker 
                         name="birthDate"
                          id="birthDate" 
                          fullWidth 
                         value={birthDate} 
                         format='dd-MM-yyyy' 
                         placeholder='Enter date' 
                         onChange={handleDateChange} 
                         label="Birth Date"
                         /> */}

                    </MuiPickersUtilsProvider>
                </GridItem>

                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Gender</FormLabel>
                    <Field fullWidth
                      id="gender"
                      name="gender"
                    //   label="Date picker inline"
                      // aria-label="gender"
                    //   value={values.gender}
                    //   error={errors.gender}
                      component={RadioGroup}
                      row

                    //   onChange={handleChange}
                    >
                    <FormControlLabel
                        id="gender"
                        label="Male"
                        control={<Radio id="gender" />}
                        value="male"
                      />
                      <FormControlLabel
                        id="gender"
                        value="female"
                        control={<Radio id="gender"/>}
                        label="Female"
                      />
                    </Field>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Marital Status</FormLabel>
                    <Field fullWidth
                      id="maritalStatus"
                      name="maritalStatus"
                      // aria-label="maritalStatus"
                    //   value={values.maritalStatus}
                    //   error={errors.maritalStatus}
                      component={RadioGroup}
                      row
                    //   onChange={handleChange}
                    >
                    <FormControlLabel
                        id="maritalStatus"
                        label="Married"
                        control={<Radio id="maritalStatus" />}
                        value="married"
                      />
                      <FormControlLabel
                        id="maritalStatus"
                        value="single"
                        control={<Radio id="maritalStatus"/>}
                        label="Single"
                      />
                    <FormControlLabel
                        id="maritalStatus"
                        value="other"
                        control={<Radio id="maritalStatus"/>}
                        label="Other"
                      />
                    </Field>
                    <Button type="submit">subit</Button>
                </GridItem>

              </GridContainer>
              </Form>
              </Formik>
            {/* </CardBody>
            <CardFooter>
              <Button color="primary">Update Profile</Button>
            </CardFooter> 
          </Card> */}
        {/* </GridItem>

      </GridContainer> */}
    </div>
    );
}

export default EmployeeInformation;
