import React, { useState, useContext } from 'react';
import { TextField } from "@material-ui/core";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import { Formik, Field, Form } from 'formik'
import { EmployeeContext } from 'views/Employee/AddEmployee'
import StepperNavigationButtons from 'views/Employee/StepperNavigationButtons';
import * as Yup from 'yup'
import { StepContext } from 'views/Employee/AddEmployee'

const ValidationSchema = Yup.object().shape({
  name1: Yup.string()
    .min(2, 'too Short!')
    .required('Required'),
  relationship1: Yup.string().required('Required'),

});

function EmergencyContact(props) {
  const [employeeData, setEmployeeData] = useContext(EmployeeContext)
  const [activeStep, setActiveStep] = useContext(StepContext);
  const [skipped, setSkipped] = useState(new Set());

  return (

      <Formik
        initialValues={employeeData}
        // validationSchema={ValidationSchema}
        onSubmit={values => {
          //handleNext()
          let newSkipped = skipped;
          const isStepSkipped = step => {
            return skipped.has(step);
          };
          if (isStepSkipped(activeStep)) {
            newSkipped = new Set(newSkipped.values());
            newSkipped.delete(activeStep);
          }
          setActiveStep(prevActiveStep => prevActiveStep + 1);
          setSkipped(newSkipped);
          setEmployeeData({
            ...employeeData,
            emergencyName1: values.name1,
            emergencyMobile1: values.mobile1,
            emergencyRelationship1: values.relationship1,
            emergencyName2: values.name2,
            emergencyMobile2: values.mobile2,
            emergencyRelationship2: values.relationship2
          })
        }}
        render={({ errors, values, touched, handleChange, handleSubmit }) => (
          <Form>
            <GridContainer>
              <GridItem xs={12} sm={12} md={12}>
                <Field
                  label="Name1"
                  id="name1"
                  name="name1"
                  value={values.name1}
                  component={TextField}
                  onChange={handleChange}
                  fullWidth
                />
              </GridItem>

              <GridItem xs={12} sm={12} md={12}>
                <Field
                  label="Mobile"
                  id="mobile1"
                  value={values.mobile1}
                  name="mobile1"
                  component={TextField}
                  onChange={handleChange}
                  fullWidth
                />
              </GridItem>

              <GridItem xs={12} sm={12} md={12}>
                <Field
                  label="Relationship"
                  id="relationship1"
                  name="relationship1"
                  value={values.relationship1}
                  component={TextField}
                  onChange={handleChange}
                  fullWidth
                />
              </GridItem>

              <GridItem xs={12} sm={12} md={12}>
                <Field
                  label="Name 2"
                  id="name2"
                  value={values.name2}
                  name="name2"
                  component={TextField}
                  onChange={handleChange}
                  fullWidth
                />
              </GridItem>

              <GridItem xs={12} sm={12} md={12}>
                <Field
                  label="Mobile"
                  id="mobile2"
                  name="mobile2"
                  value={values.mobile2}
                  component={TextField}
                  onChange={handleChange}
                  fullWidth
                />
              </GridItem>

              <GridItem xs={12} sm={12} md={12}>
                <Field
                  label="Relationship"
                  id="relationship2"
                  value={values.relationship2}
                  name="relationship2"
                  component={TextField}
                  onChange={handleChange}
                  fullWidth
                />
              </GridItem>

              <GridItem xs={12} sm={12} md={12}>
                <StepperNavigationButtons />
              </GridItem>

            </GridContainer>
          </Form>
        )}
      >
      </Formik>
  );
}

export default EmergencyContact;
