import React from 'react';
import EmployeeInformation from 'components/AddEmployeeForms/EmployeeInformation';
import { Formik } from 'formik'
import {EmployeeValidation} from 'lib/Yup/EmployeeValidation'  
import CurrentAddress from 'components/AddEmployeeForms/CurrentAddress';
import PermanentAddress from 'components/AddEmployeeForms/PermanentAddress';
import EmergencyContact from 'components/AddEmployeeForms/EmergencyContact';
import JobDetails from 'components/AddEmployeeForms/JobDetails';
import IdProof from 'components/AddEmployeeForms/IdProof';
import EmployeePicture from 'components/AddEmployeeForms/EmployeePicture';
import Feedback from 'components/AddEmployeeForms/Feedback';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles(theme => ({
    root: {
      width: '100%',
    },
    button: {
      marginRight: theme.spacing(1),
    },
    instructions: {
      marginTop: theme.spacing(1),
      marginBottom: theme.spacing(1),
    },
  }));

  function getSteps() {
    return ['Employee Information', 'Create an ad group', 'Create an ad'];
  }

  function getStepContent(step) {
    switch (step) {
        case 0:
            return 'Select campaign settings...';
        case 1:
            return 'What is an ad group anyways?';
        case 2:
            return 'This is the bit I really care about!';
        case 3:
            return 'Select campaign settings...';
        case 4:
            return 'What is an ad group anyways?';
        case 5:
            return 'This is the bit I really care about!';
        case 6:
            return 'Select campaign settings...';
        case 7:
            return 'What is an ad group anyways?';  
        default:
            return 'Unknown step';
    }
  }

// const initialValues={
//     fullname:'',
//     preferredName:'',
//     birthdate:'',
//     gender:'',
//     marital_status:'',
//     current_street1:'',
//     current_street2:'',
//     current_city:'',
//     current_state:'',
//     current_country:'',
//     current_address_proof:'',
//     permanent_street1:'',
//     permanent_street2:'',
//     permanent_city:'',
//     permanent_state:'',
//     permanent_country:'',
//     permanent_address_proof:'',
//     emergency_name1:'',
//     emergency_mobile1:'',
//     emergency_relationship1:'',
//     emergency_name2:'',
//     emergency_mobile2:'',
//     emergency_relationship2:'',
//     job_hiredate:'',
//     job_salary:'',
//     job_currentsalary:'',
//     job_bond:'',
//     id_proof:'',
//     picture:'',
//     feedback:''

// }

const initialValues={
    employeeInformation:{
        fullName:'',
        preferredName:'',
        birthDate:'',
        gender:'',
        maritalStatus:''
    },
    currentAddress:{
        street1:'',
        street2:'',
        city:'',
        state:'',
        country:'',
        addressProof:''
    },
    permanentAddress:{
        street1:'',
        street2:'',
        city:'',
        state:'',
        country:'',
        addressProof:''
    },
    emergencyContact:{
        name1:'',
        mobile1:'',
        relationship1:'',
        name2:'',
        mobile2:'',
        relationship2:''
    },
    jobDetails:{
        hireDate:'',
        salary:'',
        currentSalary:'',
        bond:''
    },
    idProof:{
        idProof:''
    },
    employeePicture:{
        picture:''
    },
    feedback:{
        feedback:''
    }    
}

function AddEmployee(props) {
    const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);
  const [skipped, setSkipped] = React.useState(new Set());
  const steps = getSteps();

  const isStepOptional = step => {
    return step === 1;
  };

  const isStepSkipped = step => {
    return skipped.has(step);
  };

  const handleNext = () => {
    let newSkipped = skipped;
    if (isStepSkipped(activeStep)) {
      newSkipped = new Set(newSkipped.values());
      newSkipped.delete(activeStep);
    }

    setActiveStep(prevActiveStep => prevActiveStep + 1);
    setSkipped(newSkipped);
  };

  const handleBack = () => {
    setActiveStep(prevActiveStep => prevActiveStep - 1);
  };

  const handleSkip = () => {
    if (!isStepOptional(activeStep)) {
      // You probably want to guard against something like this,
      // it should never occur unless someone's actively trying to break something.
      throw new Error("You can't skip a step that isn't optional.");
    }

    setActiveStep(prevActiveStep => prevActiveStep + 1);
    setSkipped(prevSkipped => {
      const newSkipped = new Set(prevSkipped.values());
      newSkipped.add(activeStep);
      return newSkipped;
    });
  };

  const handleReset = () => {
    setActiveStep(0);
  };

    return (
    <div className={classes.root}>
          <Stepper activeStep={activeStep}>
        {steps.map((label, index) => {
          const stepProps = {};
          const labelProps = {};
          if (isStepOptional(index)) {
            labelProps.optional = <Typography variant="caption">Optional</Typography>;
          }
          if (isStepSkipped(index)) {
            stepProps.completed = false;
          }
          return (
            <Step key={label} {...stepProps}>
              <StepLabel {...labelProps}>{label}</StepLabel>
            </Step>
          );
        })}
      </Stepper>
      <div>
        {activeStep === steps.length ? (
          <div>
            <Typography className={classes.instructions}>
              All steps completed - you&apos;re finished
            </Typography>
            <Button onClick={handleReset} className={classes.button}>
              Reset
            </Button>
          </div>
        ) : (
          <div>
            <Typography className={classes.instructions}>{getStepContent(activeStep)}</Typography>
            <div>
              <Button disabled={activeStep === 0} onClick={handleBack} className={classes.button}>
                Back
              </Button>
              {isStepOptional(activeStep) && (
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleSkip}
                  className={classes.button}
                >
                  Skip
                </Button>
              )}

              <Button
                variant="contained"
                color="primary"
                onClick={handleNext}
                className={classes.button}
              >
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>
            </div>
          </div>
        )}
      </div>
        {/* <Formik
          initialValues={initialValues}
          validationSchema={EmployeeValidation}
          onSubmit={values => {
            }} 
            // component={EmployeeInformation}
            // component={CurrentAddress}
            component={PermanentAddress}
            // component={EmergencyContact}
            // component={JobDetails}
            // component={IdProof}
            // component={EmployeePicture}
            // component={Feedback}
          >   
        </Formik> */}
    </div>
    );
}

export default AddEmployee;
