import React ,{useState} from 'react';
// import { makeStyles } from "@material-ui/core/styles";
import { FormLabel } from "@material-ui/core";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Dropzone from 'react-dropzone';

// const styles = {
//     cardCategoryWhite: {
//       color: "rgba(255,255,255,.62)",
//       margin: "0",
//       fontSize: "14px",
//       marginTop: "0",
//       marginBottom: "0"
//     },
//     cardTitleWhite: {
//       color: "#FFFFFF",
//       marginTop: "0px",
//       minHeight: "auto",
//       fontWeight: "300",
//       fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
//       marginBottom: "3px",
//       textDecoration: "none"
//     }
//   };

// const useStyles = makeStyles(styles);

function CurrentAddress(props) {
  const [employeeData,setEmployeeData]=useContext(employeeContext)
  setEmployeeData({...employeeData,street1:'helllo'})
    // const classes = useStyles();
    const [filename,setFilename]=useState('')
    const [file,setFile]=useState('')

    function handleOnDrop(files,rejectedFiles){
        const curfile=files[0]
        console.log(files[0].name)
        setFilename(files[0].name)

        // this.setState({imagename:files[0].name})
        const reader=new FileReader()
        reader.addEventListener("load",()=>{
            console.log("ondrop called")

        setFile(reader.result)
            // this.setState({
            //       image:reader.result
            //   })
         },false)
              reader.readAsDataURL(curfile)
    }
    return (
        <div>
        {/* <GridContainer>
        <GridItem xs={12} sm={12} md={12}>
          <Card>
            <CardHeader color="primary">
              <h4 className={classes.cardTitleWhite}>Add Employee</h4>
              <p className={classes.cardCategoryWhite}>Current Address</p>
            </CardHeader>
            <CardBody> */}
              <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="Street 1"
                    id="street1"
                    formControlProps={{
                      fullWidth: true
                    }}
                    // inputProps={{
                    //   disabled: true
                    // }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="Street 2"
                    id="street2"
                    formControlProps={{
                      fullWidth: true
                    }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="City"
                    id="city"
                    formControlProps={{
                      fullWidth: true
                    }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="State"
                    id="state"
                    formControlProps={{
                      fullWidth: true
                    }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="Country"
                    id="country"
                    formControlProps={{
                      fullWidth: true
                    }}
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Image</FormLabel>
                <Dropzone onDrop={handleOnDrop} multiple="true">

                {({getRootProps, getInputProps}) => (
                    <section>
                    <div {...getRootProps()}>
                        <input {...getInputProps()} />
                        <p>{filename===""?"Drag and drop file here, or click to select file":filename}</p>

                    </div>
                    </section>
                )}
                </Dropzone>
                </GridItem>
              </GridContainer>

            {/* </CardBody>
             <CardFooter>
              <Button color="primary">Update Profile</Button>
            </CardFooter> 
          </Card>
        </GridItem>

      </GridContainer> */}
    </div>
    );
}

export default CurrentAddress;
