import React ,{useState, useContext} from 'react';
// import { makeStyles } from "@material-ui/core/styles";
import { FormLabel,RadioGroup, Radio, FormControlLabel, Button, TextField } from "@material-ui/core";
import { Field, Form } from "formik"
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import DateFnsUtils from "@date-io/date-fns";
import { format } from "date-fns"
import { DatePicker, MuiPickersUtilsProvider} from "material-ui-pickers";
import { Formik } from 'formik'
import {EmployeeValidation} from 'lib/Yup/EmployeeValidation'  
import {EmployeeContext} from 'views/Employee/AddEmployee'
import  StepperNavigationButtons  from 'views/Employee/StepperNavigationButtons';
import * as Yup from 'yup'
import {StepContext} from 'views/Employee/AddEmployee'
// const styles = {
//     cardCategoryWhite: {
//       color: "rgba(255,255,255,.62)",
//       margin: "0",
//       fontSize: "14px",
//       marginTop: "0",
//       marginBottom: "0"
//     },
//     cardTitleWhite: {
//       color: "#FFFFFF",
//       marginTop: "0px",
//       minHeight: "auto",
//       fontWeight: "300",
//       fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
//       marginBottom: "3px",
//       textDecoration: "none"
//     }
//   };
const ValidationSchema1 = Yup.object().shape({
  fullName: Yup.string()
    .min(2,'too Short!')
    .required('Required')
  // preferredName: Yup.string(),
  // birthDate: Yup.date()
  //   .required('Required'),

}); 
const initialValues2={
  fullname:'',
preferredName:'',
birthDate:'',
gender:'',
maritalStatus:'',}
// const useStyles = makeStyles(styles);
function EmployeeInformation(props) {
    const [employeeData,setEmployeeData]=useContext(EmployeeContext)
    const [activeStep, setActiveStep] = useContext(StepContext);
    const [skipped, setSkipped] = useState(new Set());
    // const classes = useStyles();
    const [birthDate,setBirthDate]=useState(new Date())
    // console.log(employeeData)
    function handleDateChange(date){
        // setBirthDate(format(date, 'dd/MM/yyyy'))
        setBirthDate(date)
    }
    return (
        // <div>
          <Formik
            initialValues={ 
         { fullName:'',
        preferredName:'',
        birthDate:'',
        gender:'',
        maritalStatus:''}}
            // validationSchema={ValidationSchema1}
            onSubmit={value => {
              //handleNext()
              // let newSkipped = skipped;
              // const isStepSkipped = step => {
              //   return skipped.has(step);
              // };
              // if (isStepSkipped(activeStep)) {
              //   newSkipped = new Set(newSkipped.values());
              //   newSkipped.delete(activeStep);
              // }
              // setActiveStep(prevActiveStep => prevActiveStep + 1);
              // setSkipped(newSkipped);


              // alert("fd")
              // setEmployeeData({
              //   fullName:values.fullName,
              //   maritalStatus:values.maritalStatus,
              //   preferredName:values.preferredName,
              //   birthDate:values.birthDate,
              //   gender:values.gender})
              // console.log("child",employeeData)

              console.log("values",value)
              }} 

            >
               {({ errors,values, touched ,handleChange, handleSubmit}) => (
              <Form>
                {/* {console.group('actual',employeeData)} */}
              <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                  {/* we can use CustomInput also insted of field */}
                  <Field
                    label="Full Name(As on your ID)"
                    id="fullName"
                    name="fullName"
                    component={TextField}
                    fullWidth

                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <Field
                    label="Preferred Name"
                    id="preferredName"
                    name="preferredName"
                    component={TextField}
                    fullWidth
                  />
                </GridItem>

                <GridItem xs={12} sm={12} md={12}>
                    <FormLabel component="legend" style={{textAlign:'left'}}>Birth Date</FormLabel>
                    <MuiPickersUtilsProvider utils={DateFnsUtils} >
                      <Field
                      component={DatePicker}
                      value={birthDate} 
                         format='dd-MM-yyyy' 
                         placeholder='Enter date' 
                         onChange={handleDateChange} 
                        //  label="Birth Date"
                         name="birthDate"
                          id="birthDate" 
                          fullWidth
                      ></Field>
                         {/* <DatePicker 
                         name="birthDate"
                          id="birthDate" 
                          fullWidth 
                         value={birthDate} 
                         format='dd-MM-yyyy' 
                         placeholder='Enter date' 
                         onChange={handleDateChange} 
                         label="Birth Date"
                         /> */}

                    </MuiPickersUtilsProvider>
                </GridItem>

                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Gender</FormLabel>
                    <Field fullWidth
                      id="gender"
                      name="gender"

                    //   value={values.gender}
                    //   error={errors.gender}
                      component={RadioGroup}
                      row

                    //   onChange={handleChange}
                    >
                    <FormControlLabel
                        id="gender"
                        label="Male"
                        control={<Radio id="gender" />}
                        value="male"
                      />
                      <FormControlLabel
                        id="gender"
                        value="female"
                        control={<Radio id="gender"/>}
                        label="Female"
                      />
                    </Field>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Marital Status</FormLabel>
                    <Field fullWidth
                      id="maritalStatus"
                      name="maritalStatus"
                      // aria-label="maritalStatus"
                    //   value={values.maritalStatus}
                    //   error={errors.maritalStatus}
                      component={RadioGroup}
                      row
                    //   onChange={handleChange}
                    >
                    <FormControlLabel
                        id="maritalStatus"
                        label="Married"
                        control={<Radio id="maritalStatus" />}
                        value="married"
                      />
                      <FormControlLabel
                        id="maritalStatus"
                        value="single"
                        control={<Radio id="maritalStatus"/>}
                        label="Single"
                      />
                    <FormControlLabel
                        id="maritalStatus"
                        value="other"
                        control={<Radio id="maritalStatus"/>}
                        label="Other"
                      />
                    </Field>

                </GridItem>
                <GridItem>
                <StepperNavigationButtons />
                {/* <Button type="submit">fds</Button> */}
                </GridItem>
              </GridContainer>
              </Form>
               )
               }
              </Formik>
              // </div>
    );
}

export default EmployeeInformation;
