import React ,{useState,useContext} from 'react';
import { FormLabel, TextField } from "@material-ui/core";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Dropzone from 'react-dropzone';
import  StepperNavigationButtons  from 'views/Employee/StepperNavigationButtons';
import {EmployeeContext} from 'views/Employee/AddEmployee'
import * as Yup from 'yup'
import { Formik, Form, Field } from 'formik'
import {StepContext} from 'views/Employee/AddEmployee'

const ValidationSchema = Yup.object().shape({
   street1: Yup.string()
      .required('Required'),
    city: Yup.string()
      .required('Required'),
    //  addressProof: Yup.file().required('Required')

}); 
function PermanentAddress(props) {
  const [employeeData,setEmployeeData]=useContext(EmployeeContext)
  const [activeStep, setActiveStep] = useContext(StepContext);
    const [skipped, setSkipped] = useState(new Set());
    const [filename,setFilename]=useState('')
    const [file,setFile]=useState('')

    function handleOnDrop(files,rejectedFiles){
        const curfile=files[0]
        console.log(files[0].name)
        setFilename(files[0].name)

        // this.setState({imagename:files[0].name})
        const reader=new FileReader()
        reader.addEventListener("load",()=>{
            console.log("ondrop called")

        setFile(reader.result)
            // this.setState({
            //       image:reader.result
            //   })
         },false)
              reader.readAsDataURL(curfile)
    }
    return (
        <div>
          <Formik
            initialValues={employeeData}
            // validationSchema={ValidationSchema}
            onSubmit={values => {
              //handleNext()
              let newSkipped = skipped;
              const isStepSkipped = step => {
                return skipped.has(step);
              };
              if (isStepSkipped(activeStep)) {
                newSkipped = new Set(newSkipped.values());
                newSkipped.delete(activeStep);
              }
              setActiveStep(prevActiveStep => prevActiveStep + 1);
              setSkipped(newSkipped);


              alert("fd")
              setEmployeeData({...employeeData,
                permanentStreet1:values.street1,
                permanentStreet2:values.street2,
                permanentCity:values.city,
                permanentState:values.state,
                permanentCountry:values.country,
                permanentAddressProof:filename
              })
              console.log("information",employeeData)
              }} 
            render= {({errors,values, touched ,handleChange, handleSubmit}) => (
              <Form>
               <GridContainer>

                <GridItem xs={12} sm={12} md={12}>
                  <Field
                    label="Street 1"
                    id="street1"
                    name="street1"
                    value={values.street1}
                    component={TextField}
                    onChange={handleChange}
                    fullWidth


                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <Field
                    label="Street 2"
                    id="street2"
                    name="street2"
                    value={values.street2}
                    component={TextField}
                    onChange={handleChange}
                    fullWidth
                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <Field
                    label="City"
                    id="city"
                    name="city"
                    value={values.city}
                    component={TextField}
                    onChange={handleChange}
                    fullWidth

                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <Field
                    label="State"
                    id="state"
                    name="state"
                    value={values.state}
                    component={TextField}
                    onChange={handleChange}
                    fullWidth

                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <Field
                    label="Country"
                    id="country"
                    name="country"
                    value={values.country}
                    component={TextField}
                    onChange={handleChange}
                    fullWidth

                  />
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Image</FormLabel>
                <Dropzone onDrop={handleOnDrop} multiple="true">

                {({getRootProps, getInputProps}) => (
                    <section>
                    <div {...getRootProps()}>
                        <input {...getInputProps()} />
                        <p>{filename===""?"Drag and drop file here, or click to select file":filename}</p>

                    </div>
                    </section>
                )}
                </Dropzone>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                <StepperNavigationButtons/>
                </GridItem>
              </GridContainer>
              </Form>
               )
               } 
            >

              </Formik>



    </div>
    );
}

export default PermanentAddress;
