import React, { useState } from 'react';
import { Typography } from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
    root: {
        backgroundColor: '#f9f9f9',
        padding: '15px',
        borderRadius: '1%',
        // '&:focus': {
        //     backgroundColor: '#eeeeee',
        //     // border:'1px solid #eeeeee',

        //   }
    },
    expansionPanelSummary: {
        display: 'inline',
        fontSize: "14px",
        fontWeight: "200"
    },
    icon: {
        float: 'right',

        display: 'inline'
    },
    expansionPanelDetails: {
        marginTop: '20px',
        minHeight: '100px'
    }

})

function ExpansionPanel({orderstatus, createddate, ordernumber, children }) {

    const classes = useStyles()
    const [showContent, setShowContent] = useState(false)
    return (
        <div className={classes.root}>
            <Typography className={classes.expansionPanelSummary}>
                        Order Number: {ordernumber} -{" "}
                        <span>
                          {orderstatus === "Inactive" ? "Order Failed" : orderstatus} - {formatDateCommon(createddate)}
                        </span>
                      </Typography>
            {showContent ?
                <ExpandLessIcon className={classes.icon} onClick={() => { setShowContent(!showContent) }} /> :
                <ExpandMoreIcon className={classes.icon} onClick={() => { setShowContent(!showContent) }} />}
            {showContent ? <div className={classes.expansionPanelDetails}>
                    <Grid
                        key={i}
                        container
                        item
                        xs={12}
                        className="form-input-row"
                        spacing={2}>
                        <Grid item xs={12}>
                          <ProductCard
                            orderDetails={order.orderDetails[0]}
                            total={total}
                          />
                        </Grid>
                        <Grid
                          item
                          lg={6}
                          md={6}
                          sm={6}
                          xs={12}
                          className="form-input-row">
                          <Grid>
                            <h3>Shipping Address</h3>
                          </Grid>
                          <Grid className="account-summary--input-box">
                            <label>Full Name</label>
                            <TextField
                              id="user-name"
                              type="text"
                              className="common-input"
                              value={firstname + " " + lastname}
                            />
                          </Grid>
                          <Grid className="account-summary--input-box">
                            <label>Address</label>
                            <TextField
                              id="user-address"
                              type="text"
                              className="common-input"
                              value={deliveryaddress1}
                            />
                          </Grid>
                          <Grid className="account-summary--input-box account-summary--input-box-half-width mr--1">
                            <label>City</label>
                            <TextField
                              id="user-Address"
                              type="text"
                              className="common-input"
                              value={city}
                            />
                          </Grid>
                          <Grid className="account-summary--input-box account-summary--input-box-half-width">
                            <label>Post code</label>
                            <TextField
                              id="user-Address"
                              type="text"
                              className="common-input"
                              value={postcode}
                            />
                          </Grid>
                          <Grid className="account-summary--input-box">
                            <label>Country</label>
                            <TextField
                              id="user-country"
                              type="text"
                              className="common-input"
                              value={county}
                            />
                          </Grid>
                        </Grid>

                        <Grid
                          item
                          lg={6}
                          md={6}
                          sm={6}
                          xs={12}
                          className="form-input-row">
                          <h3>Delivery</h3>
                          <CustomInput
                            label="Order Reference Number"
                            value={ordernumber}
                          />
                          <CustomInput
                            label="Purchase Date"
                            value={formatDateCommon(createddate)}
                          />
                          {/* <CustomInput label="Card Ending" />
                  <CustomInput label="Delivery Partner" /> */}
                        </Grid>

                        <Grid
                          item
                          lg={6}
                          md={6}
                          sm={6}
                          xs={12}
                          className="form-input-row">
                          {orderstatus !== "Inactive" && (
                            <Fragment>
                              <h3>Dispatch Date</h3>
                              <CustomInput value={formatDateCommon(dispatchDate)} /></Fragment>)}
                        </Grid>
                        <Grid
                          item
                          lg={6}
                          md={6}
                          sm={6}
                          xs={12}
                          className="form-input-row fnl"
                          style={{ fontSize: "15px" }}>
                          <h3>Checkout Total</h3>
                          <p>SUBTOTAL:</p>
                          <div>
                            <div className="checkout_table--row">
                              <p>DELIVERY:</p>
                              <p> £{delivery_total.toFixed(2)}</p>
                            </div>
                            <div className="checkout_table--row">
                              <p>GIFT WRAPPING:</p>
                              <p> £{giftwraping_total.toFixed(2)}</p>
                            </div>
                            <div className="checkout_table--row checkout_table--total fnb">
                              <p>TOTAL:</p>
                              <p> £{total.toFixed(2)}</p>
                            </div>
                          </div>
                        </Grid>
                        <Grid container item xs={12} className="form-input-row">
                          <InputButton onClick={() => {

                            setMessageState({
                              contactemail: partner.contactemail,
                              companyName: partner.companyname,
                              ordernumber: ordernumber
                            })
                            handleOpenMessage();
                          }}>
                            Message the Seller
                        </InputButton>
                        </Grid>
                      </Grid>
            </div> : ""} 
        </div>
    );
}

export default React.memo(ExpansionPanel);
