        // setHireDate(format(date, 'dd/MM/yyyy'))
        setHireDate(date)
    }
    return (
        <div>

              <GridContainer>
              <GridItem xs={12} sm={12} md={12}>
                    {/* <FormLabel component="legend" style={{textAlign:'left'}}>Birth Date</FormLabel> */}
                    <MuiPickersUtilsProvider utils={DateFnsUtils} >
                         <DatePicker name="hireDate" id="hireDate" fullWidth 
                         value={hireDate} 
                         format='dd-MM-yyyy' 
                         placeholder='Enter date' 
                         onChange={handleDateChange} 
                         label="Hire Date"
                         helperText="Please provide date of your joining"
                         />

                    </MuiPickersUtilsProvider>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                  <TextField
                    label="Salary"
                    // placeholder="Please Provide salary when you have joined"
                    id="salary"
                    helperText="Please Provide salary when you have joined"
                    // formControlProps={{
                    //   fullWidth: true
                    // }}
                    fullWidth
                    // inputProps={{
                    //   disabled: true
                    // }}
