                  />
                </GridItem>



                <GridItem xs={12} sm={12} md={12}>
                <FormLabel component="legend" style={{textAlign:'left'}}>Have you signed/agreed any bond?</FormLabel>
                    <Field fullWidth
                      id="bond"
                    //   label="Have you signed/agreed any bond?"
                      // aria-label="gender"
                    //   value={values.gender}
                    //   error={errors.gender}
                      component={RadioGroup}
                      row

                    //   onChange={handleChange}
                    >
                    <FormControlLabel
                        id="bond"
                        label="1 Year"
                        control={<Radio id="bond" />}
                        value="1"
                      />
                      <FormControlLabel
                        id="bond"
                        value="2"
                        control={<Radio id="bond"/>}
                        label="2 Year"
                      />

                    <FormControlLabel
                        id="bond"
                        value="0"
                        control={<Radio id="bond"/>}
                        label="No"
                      />
                    </Field>
                </GridItem>

              </GridContainer>

             {/*</CardBody>
            <CardFooter>
              <Button color="primary">Update Profile</Button>
            </CardFooter> 
          </Card>
        </GridItem> */}
        {/* <GridItem xs={12} sm={12} md={4}>
          <Card profile>
            <CardAvatar profile>
              <a href="#pablo" onClick={e => e.preventDefault()}>
                <img src={avatar} alt="..." />
