import React from 'react';
import { makeStyles } from "@material-ui/core/styles";
const styles = {
    circleBackground: {
        fill: 'none',
    },
    circleProgress: {
        fill: 'none',
    },
    circleBackground: {
        stroke: '#ddd',   
    },
    circleProgress: {
        stroke: 'red',
      strokeLinecap: 'round',
      strokeLinejoin: 'round',
},
circleText: {
    fontSize: '3em',
    fontWeight: 'bold',
    fill: 'red',
  }
}

const useStyles = makeStyles(styles);


function Loader(props) {
    const classes = useStyles();
    return (
        <div>
             <svg
          width="200"
          height="200"
        //   viewBox={viewBox}
        >
          <circle
            className={classes.circleBackground}
            cx={200 / 2}
            cy={200 / 2}
            r={50}
            strokeWidth={`${20}px`}
             />
          <circle
            className={classes.circleProgress}
            cx={200 / 2}
            cy={200 / 2}
            r={50}
            strokeWidth={`${20}px`}
            // Start progress marker at 12 O'Clock
            transform={`rotate(-90 ${200 / 2} ${200 / 2})`}
            style={{
              strokeDasharray: 'dashArray',
              strokeDashoffset: 'dashOffset'
            }} 
            />
          <text
            className="circle-text"
            x="50%"
            y="50%"
            dy=".3em"
            textAnchor="middle">
            {`${200}%`}
          </text>
      </svg>
        </div>
    );
}

export default Loader;
