import * as Yup from 'yup'

export const EmployeeValidation = Yup.object().shape({
    email: Yup.string()
      .email('Enter valid Email')
      .required('Required'),
    password: Yup.string()
      .min(8, 'Too Short!')
      .required('Required')

  });
